import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import App from './app'


class APIcall extends Component
{
    constructor(props){
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items:[]
        }
    }

componentDidMount(){
    fetch("http://api.openweathermap.org/data/2.5/weather?q=Skopje&units=metric&appid=5028b23243c12ce9c01337e4bf4ea0ae")
        .then(res => res.json())
        .then(
            (result) => {
                console.log(result)
                this.setState({
                    isLoaded: true,
                    
                    items: result
                   
                });
               
            },
            (error)=>{
                this.setState({
                    isLoaded: true,
                    error
                });
            }
            )
}

    render(){
        const {error, isLoaded, items } = this.state;
        const {main} = items;
        console.log(items.main);
        if(error){
            return <div>Erorr:{error.message}</div>
        }
        else if(!isLoaded){
            return <div>Loading...</div>
        }
        else{
            return (
                <li> {main.map((item) => item.value)}</li>
            )
        }
    }

}


export default APIcall
ReactDOM.render(<APIcall />, document.getElementById('root'));
